<template>
  <div>
    <v-toolbar color="blue">
      <v-toolbar-side-icon><v-img scr="data:image/jpeg;base64,"></v-img></v-toolbar-side-icon>
      <v-toolbar-title>Library Management System</v-toolbar-title>
      <v-spacer></v-spacer>
      <v-toolbar-items class="hidden-sm-and-down">
         <h3 class="ma-3" style="color:white">{{welcomeMessage}}</h3>
         <router-link :to="{path:home}"><h3 class="ma-3">Home</h3></router-link>
         <router-link to="/aboutus"><h3 class="ma-3">About US</h3></router-link>
        
         <v-layout v-if="userLoggedInStatus=='inactive'">
         <router-link to="/signin"><h3 class="ma-3">Sign In</h3></router-link>
         </v-layout>   
         <router-link v-else to="/logout"><h3 class="ma-3">Logout</h3></router-link>
         
      </v-toolbar-items>
    </v-toolbar>
  </div>
</template>
<script>
export default {
  data(){
    return{
        
    }
  },
  computed:{
    home:{
            get(){
              if(this.userLoggedInStatus=='active')
              return '/home'
              else
              return '/'
            }
    },
    userLoggedInStatus:{
      get()
      {
        return this.$store.getters['getUserLoggedInStatus']
      }
    },
    jwtToken:{
      get(){
          return localStorage.getItem("jwtToken")
      }
    },
    welcomeMessage:{
      get(){
        return this.$store.state.welcomeMessage
      }
    }
  }
};
</script>
